document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".swiper-gallery").forEach((e=>{new Swiper(e,{loop:!0,slidesPerView:"auto",spaceBetween:2,centeredSlides:!1,speed:2e4,autoplay:{delay:0,disableOnInteraction:!1},grabCursor:!0,allowTouchMove:!0,freeMode:{enabled:!0,momentum:!1,sticky:!0}})}))}));